$.ajaxSetup({
    headers: {
        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
    }
});
$(document).ready(function(){

    $('.submit_remove').click(function(){
        let id = $(this).data('id');
        $('.delete_general').click(function(){
            $.ajax({
                type : 'delete',
                url : 'generals/'+id,
                dataType : 'JSON',
                data : { id : id },
                success:function(data){
                    location.reload();
                   
                }
            });
        });
    });
    
    $('.submit_edit').click(function(e){
        e.preventDefault();
        let id = $(this).data('id');
        $.ajax({
            type : 'get',
            url : 'generals/'+id+'/edit',
            dataType : 'json',
            data : { id : id },
            success:function(data){
                $('#view_update_title').html(data);
            }
        });
    });

    $('.qr_code').click(function(){
        let id = $(this).data('id');
        $.ajax({
            type : 'get',
            url : 'qrcode/events',
            dataType : 'Json',
            data : { id:id },
            success:function(data){
                $('#qrcode').html(data);
            }
        });
    });


    
});